/*

	Fluid dynamics controls velocity field.
	Particles translate with the velocity field.
	Particles reproduce as they move. 
	Particles diffuse  4  hormones. 
	Diffusion is mediated by a 2 pass multi-scale gaussian blur.
	Particles experience a force from each hormone.
	The force is proportional to their own hormone signature. 
	Each hormone diffuses with a different radius. 
	Each initial particle has its own hormone signature.
	Then they battle it out! 

*/
void mainImage( out vec4 Q, in vec2 U)
{
    vec4 b = B(U);
    vec4 h = (hash(b.w));
    Q = smoothstep(2.,0.,length(b.xy-U))*(.5+2.*h);
	//Q = abs(D(U));
}

#define R iResolution.xy
#define A(U) texture(iChannel0, (U)/R)
#define B(U) texture(iChannel1, (U)/R)
#define C(U) texture(iChannel2, (U)/R)
#define D(U) texture(iChannel3, (U)/R)


#define S vec4(22,444,66,8)
#define M .1*vec4(4,14,2,14)
#define O .5/S/S
#define I 12.

vec4 hash (float p) // Dave (Hash)kins
{
	vec4 p4 = fract(vec4(p) * vec4(.0031, .9030, .0973, .0099));
    p4 += dot(p4, p4.wzxy+111.119);
    return fract((p4.xxyz+p4.yzzw)*p4.zywx)*2.-1.;

}

// FLUID DYNAMICS
// FORCE on FLUID = (PARTICLE)*(GRADIENT OF BUFFER D)

#define R iResolution.xy
#define A(U) texture(iChannel0, (U)/R)
#define B(U) texture(iChannel1, (U)/R)
vec4 T (vec2 U) {return A(U-A(U).xy);}
void mainImage( out vec4 Q, in vec2 U)
{
    Q = T(U);
    vec4 // neighborhood
        n = T(U+vec2(0,1)),
        e = T(U+vec2(1,0)),
        s = T(U-vec2(0,1)),
        w = T(U-vec2(1,0));
   // FLUID DYNAMICS
   Q.x -= (0.25*(e.z-w.z-Q.w*(n.w-s.w)));
   Q.y -= (0.25*(n.z-s.z-Q.w*(e.w-w.w)));
   Q.z += (0.25*((s.y-n.y+w.x-e.x)+(n.z+e.z+s.z+w.z))-Q.z);
   Q.w += (0.25*(s.x-n.x+w.y-e.y)-Q.w);
   // COMPUTE HORMONE FEILD
   n = D(U+vec2(0,1));
   e = D(U+vec2(1,0));
   s = D(U-vec2(0,1));
   w = D(U-vec2(1,0));
   // THIS PARTICLE
   vec4 b = B(U);
   // COMPUTE HORMONE SIGNATURE
   vec4 h = hash(b.w);
   // SUM HORMONE FORCE
   vec2 v = vec2(0);
   v += h.x*vec2(e.x-w.x,n.x-s.x);
   v += h.y*vec2(e.y-w.y,n.y-s.y);
   v += h.z*vec2(e.z-w.z,n.z-s.z);
   v += h.w*vec2(e.w-w.w,n.w-s.w);
   // APPLY HORMONE FORCE TO THIS PARTICLE
   Q.xy += v*smoothstep(1.,0.,length(U-b.xy));
   // BOUNDARY CONDITIONS
   if (fract(.1*iTime)<.2)Q.xy += .03*smoothstep(20.,0.,length(U-0.5*R+0.3*R*hash(floor(.1*iTime)).xy))*vec2(cos(floor(.1*iTime)),sin(floor(.1*iTime)));
   if (U.x<1.||U.y<1.||R.x-U.x<1.||R.y-U.y<1.||iFrame<1)
       Q.xyw = vec3(0);

}

// SPACIALLY SORT VORONOI PARTICLES
// ALLOW MOVING PARTICLES TO LEAVE A TRAIL OF CLONES
void swap (inout vec4 Q, vec2 U, vec2 r) {
	vec4 n = B(U+r);
    if (length(U-n.xy)<length(U-Q.xy)) Q = n;
}
void mainImage( out vec4 Q, in vec2 U)
{
    // FIND NEAREST PARTICLE
    Q = B(U);
    swap(Q,U,vec2(11,0));
    swap(Q,U,vec2(0,100));
    swap(Q,U,vec2(-5,0));
    swap(Q,U,vec2(0,-8));
    swap(Q,U,vec2(1,1));
    swap(Q,U,vec2(5,-1));
    swap(Q,U,vec2(-1,22));
    swap(Q,U,vec2(-22,-9));
    // LEAVE A TRIAL OF CLONES AS PARTICLE TRANSLATES
    Q.xy += A(mix(U,Q.xy,0.1)).xy;
    // BOUNDARY CONDITIONS
    if ((iMouse.z>0.&&length(iMouse.xy-U)<1.)||iFrame < 1) {
    	Q = vec4(U,0,0);
        Q.w = .1*(Q.x+R.x*Q.y+dot(iDate,vec4(1)));
    }

}

// BLUR PARTICLES PASS 1
void mainImage( out vec4 Q, in vec2 U )
{
    Q = vec4(0);
    for (float i = -I; i <= I; i++) {
        vec2 x = U+vec2(i,0);
        vec4 b = B(x);
    	Q += hash(b.w)*M*exp(-i*i*O)*smoothstep(0.,0.,length(b.xy-x));
    }

}

// BLUR PASS 2
void mainImage( out vec4 Q, in vec2 U )
{
    Q = 0.5*D(U);
    for (float i = -I; i <= I; i++) {
        vec4 c = C(U+vec2(0,i));
    	Q += c*M*exp(-O*i*i);
    }
    if(iFrame<1) Q = vec4(0);
}
