void mainImage(out vec4 o,vec2 u){
    o = vec4(0.1);
    vec2 d = 1.-abs((texelFetch(iChannel0,ivec2(0),0).xy-(u/iResolution.xy))*iResolution.xy/2.);
    o += clamp(max(d.x,d.y),0.,1.)*vec4(1.000,0.251,0.000,0);
    o += texelFetch(iChannel1,ivec2(u),0)*vec4(0.000,0.482,1.000,0);
}

void mainImage(out vec4 o,vec2 u){
    o = vec4(0);
    vec2 uv = (u+u-iResolution.xy)/iResolution.y;
    vec2 pos = vec2(sin(iTime),cos(iTime))*0.8;
    o = vec4(clamp((.1-length(uv-pos))*iResolution.y/2.,0.,1.));
}

void mainImage(out vec4 o,vec2 u){
    if(ivec2(u)==ivec2(0)){
        float c = 0.;
        vec2 pos = vec2(0);
        float i = 30.;
        for(float ix = 0.;ix<i;ix++){
            for(float iy = 0.;iy<i;iy++){
                vec2 t = vec2(ix,iy)/i;
                bool b = dot(texture(iChannel0,t).xyz,vec3(1))/3.>0.8;
                if(b){
                    pos += t;
                    c++;
                }
            }
        }
        pos /= c;
        if(iFrame>0)o = mix(texelFetch(iChannel1,ivec2(0),0),vec4(pos,0,0),0.5);
        else o = vec4(0);
    }
}
