// Golfing https://shadertoy.com/view/WltBzs ( 866 chars )
// Short version of "random pixel sprites" by stb. https://shadertoy.com/view/3ttfzl ( 2371 chars )


#define H(p)     fract( sin(p) * 4e3 )
#define h(p)     H( (p+floor(q)) * mat2x3(R,K,.7*R) )
#define N(p,z) ( q = vec3(p,z) / .1,                     \
                 f = fract(q), f *= f*(3.-f-f),          \
                 mix( mix( h()     , h(o.xyy), f.x ),    \
                      mix( h(o.yxy), h(o.xxy), f.x ),    \
                      f.y ) - .5                         \
               )

void mainImage( out vec4 O, vec2 p ) {
    vec2 R = iResolution.xy, K = R.yx, o = vec2(1,0);
    vec3 q,f;
	p = ( p - iMouse.xy ) / 72.;   
    p.y += .5 * iTime; 

    float z = 54. * H( dot( ceil(p), R ) );

    p = fract( ceil(p*24.) / 24. ) - .5; 
    p.x = abs(p.x); 

#define M(p)   length( p - .3* N(p,z) ) > .27 
    bool m = M(p), b = m;
    for ( int k=0; k<9; k++ )
        b = b && M( p - vec2(k%3-1,k/3-1) / 24. );    

     O.rgb =  b||m  ? vec3( b )
                    : pow( H(  vec3( ceil( p/.213  - 2.17*N(p,0) - z ), z )
                             * mat3(R,K,.7*K,.7*R,.9*R)), 
                           vec3(.75) ); 
}






