vec3 hsv2rgb(vec3 c)
{
    vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    // Output to screen
    fragColor = vec4(hsv2rgb(vec3(texture(iChannel0, uv).x,1.0f,1.0f)),1.0f);    
    fragColor = texture(iChannel0, uv);

}

vec2 getUV(vec2 coord) {
    return coord/iResolution.xy;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float reach = 1.0f;
    fragColor = texture(iChannel0, getUV(fragCoord.xy))
        +texture(iChannel0, getUV(fragCoord.xy+vec2(1.0f,0.0f)*reach))
        +texture(iChannel0, getUV(fragCoord.xy+vec2(-1.0f,0.0f)*reach))
        +texture(iChannel0, getUV(fragCoord.xy+vec2(0.0f,1.0f)*reach))
        +texture(iChannel0, getUV(fragCoord.xy+vec2(0.0f,-1.0f)*reach))
        +texture(iChannel0, getUV(fragCoord.xy+vec2(1.0f,1.0f)*reach))
        +texture(iChannel0, getUV(fragCoord.xy+vec2(-1.0f,1.0f)*reach))
        +texture(iChannel0, getUV(fragCoord.xy+vec2(-1.0f,-1.0f)*reach))
        +texture(iChannel0, getUV(fragCoord.xy+vec2(1.0f,-1.0f)*reach));
    fragColor = fragColor / 9.0f;
    if(iMouse.z > 0.0f) {
        if(length(fragCoord.xy-iMouse.xy) < 10.0f) {

            fragColor = vec4(vec3(1.0f,1.0f,1.0f),1.0f);
        }        
    }


}
