void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 xy = fragCoord.xy/iResolution.xy;
    vec4 texColor = texture(iChannel0,xy);
    float rgb =  (texColor.r +  texColor.g + texColor.b)/3.0;

    float rd = rgb - texColor.r;
    float gr = rgb - texColor.g;
    float bl = rgb - texColor.b;
    float dt = (cos(iTime) + 1.0)/2.0;

    texColor.r = texColor.r + dt*rd;
    texColor.g = texColor.g + dt*gr;
    texColor.b = texColor.b + dt*bl;
    fragColor = texColor;
}
