//tutorial https://www.youtube.com/watch?v=VmrIDyYiJBA

float HexDist(vec2 uv){
  uv=abs(uv);
  float c = dot (uv, normalize(vec2(1,1.73)));// 1.73 is from sqrt of (2 to the power of 2 - 1 to the power of 2)
  c=max(c,uv.x);

   return c;
}

mat2 rotate2d(float _angle){
    return mat2(cos(_angle),-sin(_angle),
                sin(_angle),cos(_angle));
}

vec4 HexCoords(vec2 uv){
    vec2 r = vec2(1.,1.73);
    vec2 h = r/2.;

    vec2 a = mod(uv,r)-h;
    vec2 b = mod(uv-h,r)-h;

    vec2 gv = dot(a,a)<dot(b,b)?a:b; // shorter version of if statement 

    vec2 id = (uv-gv)*rotate2d(3.1415*cos(iTime/100.));

    float x = atan(gv.x,gv.y);// which angle the x is at according to the center of y
    float y = 0.5-HexDist(gv);// center of the hex

    return vec4(x,y,id.x,id.y);

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-0.5*iResolution.xy)/iResolution.y;

    vec3 col = vec3(0);

    uv*=8.;
    vec4 hex= HexCoords(uv);
    float c = smoothstep(.05,1.,hex.y*sin(hex.x+hex.z+iTime*0.6));
    col+=c;

    col*=abs(uv.y-uv.x);  


    /**
    Moving Hex
    col+=sin(HexDist(uv*10.+iTime));

    */

    fragColor = vec4(col*(abs(sin(iTime*0.5))+0.1),1.0);
}
