float time1(float s) {
    return fract((iTime*s+.4));
}

float hash2(vec2 p)
{
	return fract(sin(dot(p, vec2(3.97531, 7.2357))) * 135.54321);
}

float shash2(vec2 p)
{
	return (hash2(p)*2.-1.);
}

vec2 vshash2(vec2 p){
    return vec2(shash2(p+.12357),shash2(p));
}

void drawImage (inout vec4 c, vec2 p, vec2 t, vec2 l, vec2 s, float a )
{
    mat2 rot = mat2(cos(a),sin(a),-sin(a),cos(a));
    vec2 tr = t+(s-l)/2.;
    vec2 pt = (p - tr)*rot + tr - t;
    vec2 pl = pt;
    vec2 sl = s-l;
    if (step(vec2(0.),pl)*step(pl, sl) == vec2(1.0)){
        c =  texture(iChannel0, pt + l);
        vec2 cc = smoothstep(vec2(0.002),vec2(0.00),pl);
        cc += smoothstep(sl-vec2(0.002),pl,sl);
        float b = (cc.x+cc.y);
        c = mix(c,vec4(.3),b*.5);
    } 
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    const float s = 10.;
    const float sx = s;
    const float sy = s;

    float t = iTime;
    float tf = fract(t*.2);

    vec4 col = texture(iChannel1, uv)*.3;

    float tcb = pow(abs(time1(.02)*2.0-1.0),1.5)*2.;
    float tc = pow(abs(time1(.02)*2.0-1.0),3.)*2.;

    for (int i = 0; i < int(sx); i++)
    for (int j = 0; j < int(sy); j++) 
    {
        vec2 pv = vec2(i,j);
        vec2 pf = pv/vec2(sx,sy);
        vec2 ps = 1./vec2(sx,sy);

        vec2 pm = tc*vshash2(pv)*5.;
        float a = tcb*(pm.x+pm.y*pm.y)*35.;

        drawImage(col, uv, pf+pm, pf, ps+pf, a);
     }

    col = mix(col, vec4(0.),length(uv-.5)-.2);
    fragColor = col;

}
