// variant of https://shadertoy.com/view/tllGD4

// #define S(v) min( 1., abs(v)/p  )    // lazy AA

void mainImage( out vec4 O, vec2 u )
{
    vec2 R = iResolution.xy,
         U = 16.* ( u+u - R ) / R.y, P,V, C = vec2(1,0);
    float l,r, p = 90./R.y, _F=0.,F=1.,f;
    O = vec4(1);

    for (int i=0; i<9; i++) {
        V = cos( 1.5708 * vec2(i+1,i) );// next box direction
        r = F/2.;                       // box radius & center
        P = C - r* vec2(V.y+V.x,V.y-V.x);  // vec2(1)*mat2(V.y,V.x,-V.x,V.y);
        P = abs( P - U );
        l = max(P.x,P.y) - r;           // SDF to box border
        if ( l < p/4.) {                // in box: draw arc and box
            O = mix(vec4(1,0,0,1), .98+.02*cos( float(i)  + vec4(0,23,21,0)  ), min(1.,abs(length(C-U)-2.*r)/p) ); // https://www.shadertoy.com/view/ll2cDc
            O = min(O, abs(l)*2./p );
        }
        C += _F * V;                    // next arc center
        f=F; F += _F; _F=f;             // Fibonacci sequence
    }
}
