void mainImage(out vec4 fragColor, in vec2 fragCoord) {    
    vec2 uv = fragCoord.xy/iResolution.xy;

    vec2 c = vec2(uv.x * 3.5 - 2.5, uv.y * 2. - 1.);
    vec2 z = vec2(0.);
    int i = 0;
    const int max_i = 100;

    while(z.x * z.x + z.y * z.y < 4. && i <= max_i) {
        float x_ = z.x * z.x - z.y * z.y + c.x;
        z.y = 2. * z.x * z.y + c.y;
        z.x = x_;
        ++i;
    }

	if (i == max_i)
        fragColor = vec4(vec3(0.0),1.0);
    else
        fragColor = vec4(vec3(1. - float(i)/float(max_i)), 1.);
}
