vec2 rotate2D (vec2 _st, float _angle) {
    _st -= 0.5;
    _st =  mat2(cos(_angle),-sin(_angle),
                sin(_angle),cos(_angle)) * _st;
    _st += 0.5;
    return _st;
}


float layer(vec2 uv, vec2 offset, float s, float t){
   uv += offset * t * 100.4;
   uv = rotate2D(uv, t);
   float p = mod(uv.x/  s, 1.0);
  // return smoothstep(p, p * 1.01, 0.5);
    return p;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec2 f = fragCoord.xy;

    float t = iTime * 0.01;
    vec2 u = uv;

    float a = layer(f, vec2(0.0, 3.0), 500.0, t * -4.4);
    float b = layer(f, vec2(3.3, 0.5), 100.0, t * 2.432);
    float c = layer(f, vec2(2.2, -3.3), 50.0, t * -1.32);

    float k = a - b + c;

    fragColor = vec4(k);
}
