const vec3 HASHSCALE3 = vec3(.1031, .1030, .0973);
const float SIMULATION_SPEED = 8.0;
vec2 points[10];
vec4 COLORS[10];

// Random 1 -> 2
vec2 hash21(float p) {
	vec3 p3 = fract(vec3(p) * vec3(.1031, .1030, .0973));
	p3 += dot(p3, p3.yzx + 19.19);
    return fract((p3.xx+p3.yz)*p3.zy);

}

// Random 2 -> 2
vec2 hash22(vec2 p) {
	vec3 p3 = fract(vec3(p.xyx) * HASHSCALE3);
    p3 += dot(p3, p3.yzx+19.19);
    return fract((p3.xx+p3.yz)*p3.zy);

}

void init(){
    for (int i = 0; i < 10; i++)
        points[i] = hash21(float(i)+1.);

    for (int i = 0; i < 10; i++)
        COLORS[i] = vec4(float(1/(i+1)),float(1/(i+2)),float(1/(i+3)),float(1/(i+4))) ;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    init();
    vec2 uv = (fragCoord/iResolution.xy) * vec2(iResolution.x/iResolution.y, 1.0);
    float dist = 99999999.;
    int index = 0;
    float l = 0.;
    for (int i = 0; i < 10; i++){
    	l = sqrt((fragCoord.x + points[i].x)*(fragCoord.x + points[i].x) + (fragCoord.y + points[i].y)*(fragCoord.y + points[i].y));
        if (l < dist){
                dist = l;
                index = i;
        }
    }

    vec4 color = vec4(hash22(uv), hash21(uv.x).y, 1.0);
    fragColor = COLORS[index];  
}
