// Referrence:
// [2TC 15] Mystery Mountains.
// David Hoskins.

// Add texture layers of differing frequencies and magnitudes...
#define F +texture(iChannel0,.3+p.xz*s*0.0001)/(s+=s) 

void mainImage( out vec4 c, vec2 w )
{
    vec4 p = vec4(w/iResolution.xy, 1, 1) - .5;
    vec4 d = p;
    d.y -= .07;
    p.z += (iTime + 90.) * 5.;

    vec4 t;
    vec4 color;
    for(float i = 1.5; i > 0.; i -= .002)
    {            
        float s =.5;

        //height  //p.xz = (x,time), s = amplitude
        t = F F F F;

        // put abs() around d.x to prevent black screen on some device
        // Thanks @dean_the_coder
        color = t*i + pow(abs(d.x), 4.);

        if(t.x > p.y*.007+1.2)
        {
            break;
        }
        p += d;
    }

    float white = smoothstep(0.9, 2., color.x+color.y+color.z);

    color = 1. - pow(color, vec4(0.8));
    color.x += .05;
    color.y += .1;
    color.z += .35;

    color = mix(vec4(0.75, 0.3, 0.22, 0), color, white);


    vec2 st = w/iResolution.xy;
    st.x *= iResolution.x/iResolution.y;
    float circle = distance(st, vec2(.5, .55));
    circle = step(circle, 0.2);
    circle *= pow(st.y, 4.) * 2.5;
    circle *= (1. - white);

    color = mix(color, vec4(0., 0.7, 0.6, 0), circle);

    c = color;
}
