void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec3 tex2 = texture(iChannel2, uv).xyz;

	uv.x += ((sin(iTime/9.)+1.) / 2.) * (
        cos((iTime+3.*tex2.r))
    );


    uv.y += ((cos(iTime/50.)+1.) / 2.) * (
        sin((iTime/10.+3.*uv.x*tex2.b)) * 
         cos((iTime/20.+2.*tex2.g))
    );


    vec4 oricol = texture(iChannel1, uv).rgba;

    // Output to screen
    fragColor = oricol;
}
