void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec3 old_rgb = texture(iChannel1, uv).rgb;
    vec3 new_rgb = texture(iChannel0, uv).rgb;
    float diff = step(0.05, length(old_rgb - new_rgb));
    vec3 accum_rgb = texture(iChannel2, uv).rgb;
    fragColor = vec4(mix(accum_rgb, 3.0 * new_rgb - 2.0 * old_rgb, diff),1.0);
}

