void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec2 q = uv - vec2(0.3, 0.7);

    // sunset
    vec3 col = mix(vec3(1.0, 0.4, 0.1), vec3(1.0, 0.8, 0.3), pow(uv.y, 0.7));

    float r = 0.2 + 0.1*cos(atan(q.y,q.x)*9.0 + 20.0*q.x + 2.0);
    col *= smoothstep(r, r+0.001, length(q));

    r = 0.015;
   	r += 0.002*cos(120.0*q.y); // wavy ridge in trunck
    r += exp(-40.0*uv.y); // land
    col *= 1.0 - (1.0-smoothstep(r, r+0.002, abs(q.x-0.2*sin(2.0*q.y)))) * (1.0-smoothstep(0.0,0.1,q.y));

    fragColor = vec4(col,1.0);
}
