float small_angle_approx_cos(float x) {
    return 1.0-0.5*x*x;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
	float start = radians(0.);
	float end = radians (5.);   
    float res_start = cos(start);
    float res_end = cos(end);
    float x = mix(start, end, uv.x);
    float c = cos(x);
    float c2 = small_angle_approx_cos(x);

    float col = mix(res_end, res_start, uv.y)>c ? 1.0 : 0.0;
    float col2 = mix(res_end, res_start, uv.y)>c2 ? 1.0 : 0.0;
    float col3 = (c == 1.) ? 1.0 : 0.0;

    fragColor = vec4(col, col2, col3, 1.0);
}
