void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 firePosition = vec2(0., 0.0);
    vec3 fireBallColor = vec3(0.8, 0.2, 0.1);
    float ballSize = 0.3;
    float fireSpeed = 1.5;

    // Normalized pixel coordinates (from -0.5 to 0.5)
    vec2 uv = fragCoord/iResolution.xy - 0.5;
    // Fix aspect ratio
    uv.x *= iResolution.x / iResolution.y;

   	float ballDistance = distance(firePosition, vec2(uv.x, uv.y));
    vec3 fireBall = (max(sin(1. - sin(1. - iTime * 2.)) + 3.8, 1.))
        * smoothstep(ballSize, 0.0, ballDistance)
        * fireBallColor * vec3(smoothstep(ballSize, ballSize - 0.02, ballDistance));

    vec3 col = vec3(fireBall);


    vec3 flameColor = vec3(0.);

    float i;
	for(i = 0.; i < .6; i += .1) 
    {
    	flameColor += texture(iChannel1, vec2((uv.x - 1.) * .6 + i , iTime * fireSpeed * 0.2 - uv.y + i)).rrr;
    }
    flameColor *= vec3(0.6, 0., 0.);


   	flameColor += 0.3 * texture(iChannel1, vec2((uv.x - 1.) * .6, iTime * fireSpeed * 0.2 - uv.y)).rrr;
    flameColor *= smoothstep(firePosition.y + .6, firePosition.y, uv.y);
    flameColor += 0.3 * texture(iChannel0, vec2((uv.x - 1.) * 2.6, iTime * fireSpeed * 0.2 - uv.y)).rrr;
    flameColor *= smoothstep(firePosition.y + .8, firePosition.y, uv.y);


    flameColor += texture(iChannel0, vec2((uv.x - 1.) * 2.6, iTime * fireSpeed / 2. - uv.y)).rrr;

    flameColor.r += 0.3;
    if (flameColor.r < 0.2) flameColor.r = 0.5;
    flameColor *= smoothstep(firePosition.x + 0.25, firePosition.x, uv.x);        
    flameColor *= smoothstep(firePosition.x - 0.25, firePosition.x, uv.x);
    flameColor *= smoothstep(firePosition.y - 0.15, firePosition.y, uv.y);
    flameColor *= smoothstep(0.6, 0., distance(uv, firePosition));
    flameColor *= vec3(1.2, 0., 0.);


    col += flameColor;

    // Output to screen
    fragColor = vec4(col,1.0);
}
