// https://www.shadertoy.com/view/4djSRW
vec3 hash31(float p)
{
   vec3 p3 = fract(vec3(p) * vec3(.1031, .1030, .0973));
   p3 += dot(p3, p3.yzx+33.33);
   return fract((p3.xxy+p3.yzz)*p3.zyx); 
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec3 col;
    float t = floor(iTime * 0.5);
    vec3 c0 = hash31(t + 12.512462);
    vec3 c1 = hash31(t + 6.063512);
    vec3 c2 = hash31(t + 62.872452);
    vec3 c3 = hash31(t + 2.106431);
    vec3 c4 = hash31(t + 84.965352);

    vec3 cFrom = uv.x < 0.25 ? c0 : (uv.x < 0.5 ? c1 : (uv.x < 0.75 ? c2 : c3 ));
    vec3 cTo = uv.x < 0.25 ? c1 : (uv.x < 0.5 ? c2 : (uv.x < 0.75 ? c3 : c4 ));
    float ratio = mod(uv.x, 0.25) * 4.0;
    if (uv.y > 0.5) {
        // linear
        col = mix(cFrom, cTo, ratio);
    } else {
        // smoothstep
        col = mix(cFrom, cTo, smoothstep(0.0, 1.0, ratio));
    }

    // Output to screen
    fragColor = vec4(col,1.0);
}
