void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec3 new_rgb = texture(iChannel1, uv).rgb;
    vec4 result = texture(iChannel0, uv);
    vec3 old_rgb = result.rgb;

    if (length(old_rgb) >  length(new_rgb)) {
		result.rgb = new_rgb;
    } else {
        result.rgb = mix(old_rgb, new_rgb, 0.01);
    }

    fragColor = result;
}
