#define R iResolution.xy
#define a(U) texture(iChannel0,(U)/R)
#define b(U) texture(iChannel1,(U)/R)
void mainImage( out vec4 Q, vec2 U )
{
    vec4 A = a(U);
    vec4 
        n = a(U+vec2(0,1)),
        e = a(U+vec2(1,0)),
        s = a(U-vec2(0,1)),
        w = a(U-vec2(1,0)),
        u = 0.25*(n+e+s+w), 
        l = u-A;
   	vec3 g = normalize(vec3(e.w-w.w,n.w-s.w,1)),
         r = reflect(g,vec3(0,0,1));

    Q = (.5+.5*max(0.,dot(r,normalize(vec3(2,1,-1)))))*(.3+max(sin(5.31+l+2.*atan(4.*A.w)*vec4(1,-1,2,4)),-.2));
}

#define R iResolution.xy
#define a(U) texture(iChannel0,(U)/R)
vec4 A (vec2 U) {
	return a(U-a(U).xy);
}
#define B(U) texture(iChannel1,(U)/R)
void mainImage( out vec4 Q, vec2 U )
{
    Q = A(U);
    vec4 
        b = B(U),
        n = A(U+vec2(0,1)),
        e = A(U+vec2(1,0)),
        s = A(U-vec2(0,1)),
        w = A(U-vec2(1,0)),
        nb = B(U+vec2(0,1)),
        eb = B(U+vec2(1,0)),
        sb = B(U-vec2(0,1)),
        wb = B(U-vec2(1,0));
    Q.z = 0.25*(n.z+e.z+s.z+w.z+e.x-w.x+n.y-s.y);
    Q.x += 0.25*(e.z-w.z)+Q.w*k*b.x;
    Q.y += 0.25*(n.z-s.z)+Q.w*k*b.y;
    Q.w -= 0.25*(e.w*e.x-w.w*w.x+n.w*n.y-s.w*s.y);
    Q.y += c*(p-Q.w);
    if (iMouse.z>0.&&length(U-iMouse.xy)<10.) {Q.w = 1.;}
    if (iFrame < 1) Q.w = 0.5-0.5*sign(U.y-30.);
    if (U.x<5.||U.y<5.||R.x-U.x<5.||R.y-U.y<5.)  Q*= 0.;


    Q.xy = clamp(Q.xy,-1.,1.);
}

#define R iResolution.xy
#define aa(U) texture(iChannel0,(U)/R)
vec4 A (vec2 U) {
	return aa(U-aa(U).xy);
}
#define b(U) texture(iChannel1,(U)/R)
vec4 B (vec2 U) {
	return b(U-aa(U).xy);
}
void mainImage( out vec4 Q, vec2 U )
{
    Q = B(U);
    vec4 a = A(U);
    vec4 
        n = A(U+vec2(0,1)),
        e = A(U+vec2(1,0)),
        s = A(U-vec2(0,1)),
        w = A(U-vec2(1,0));
    Q.xy += a.w*k*(0.25*(n+e+s+w).xy-m*a.xy);

    Q.w = (a.w+n.w+e.w+s.w+w.w)/5.;
}

