vec2 rotate(vec2 v, float alpha);
float line(vec2 p1, vec2 p2, vec2 p, float thickness);

#define SHOW_LINES

void mainImage( out vec4 o, in vec2 f )
{
    vec2	R = iResolution.xy;
    vec2	u = 1.*(f.xy-.5*R) / R.y;
    vec3 idl;    
    float md = 1e5;

    for( float i = .0; i < NUM*NUM; i++)
    {
        vec2 cd = vec2(floor((i)/NUM) , mod(i, NUM) );
        vec2 pos = texture(iChannel0, cd/R).xy-vec2((cd.x <= 0. ? .125:.0), (cd.y <= 0. ? .125:.0) );
        float bl = length(u - pos*SC)-9./R.x;
    	md = min(md,
                 bl
                 );
        if (md == bl && md < 4./R.x)
        idl.x = i+floor(iTime);
        #ifdef SHOW_LINES
        vec2 apos = texture(iChannel0, vec2(cd.x+0., cd.y-1.)/R ).xy-vec2((cd.x <= 0. ? .125:.0), (cd.y-1. <= 0. ? .125:.0) );
        float l0 = 1.-(line(apos*SC, pos*SC, u, 4./R.x) );
        		        md = min(md,
		                 l0
                        );
        apos = texture(iChannel0, vec2(cd.x-1., cd.y-0.)/R ).xy-vec2((cd.x-1. <= 0. ? .125:.0), (cd.y <= 0. ? .125:.0) );
        float l1 = 1.-(line(apos*SC, pos*SC, u, 4./R.x));
        md = min(md,
		                 l1
                        );        
        #endif
        if (md < 0.001)
            break;
    }
//    o.xyz = texture(iChannel0, .05*f.xy/R.xy).xyz;
    o.xyz = .75-1.*vec3(smoothstep( .01, .005, md))*1.0*(sin( idl.x*.5*vec3(0.5, 1., 2.) +1.570 ) );
}

vec2 rotate(vec2 v, float alpha)
{
	float vx = v.x*cos(alpha)-v.y*sin(alpha);
	float vy = v.x*sin(alpha)+v.y*cos(alpha);
	v.x = vx;
	v.y = vy;
	return v;
}

float line(vec2 p1, vec2 p2, vec2 p, float thickness)
{
	p -= p1;
	vec2 lineVector = p2-p1;

	float angle = -atan(lineVector.y,lineVector.x);
	p = rotate(p,angle);

	float dx = 0.0;
	if(p.x<0.0)
		dx = abs(p.x);
	else if(p.x>length(lineVector))
		dx = abs(p.x) - length(lineVector);

	return ( thickness/(dx+abs(p.y)) );
}

/*
* BUFFER A : 2D Particle System
*/


// for reading clarity's sake, not rly useful
struct	particle
{
    vec2	pos;
    vec2	acc;
    vec2	vit;
};

#define Ma 1.
#define Gr vec2(.0*.125*cos(iTime*1.+sin(iTime*2.)*6.28), -.98)
#define Xrs 1. // X rest straight
#define Xrd sqrt(2.) // X rest diagonal
#define DIST .3

    void	init_pos(inout vec2 pos, vec2 ij)
{
    pos.x = ij.x*DIST-.5*NUM*DIST;
    pos.y = ij.y*DIST-.5*NUM*DIST;
}

void	init(inout particle part)
{
    part.acc = vec2(0.);
	part.vit.xy = vec2(0.);
}

vec2 DistanceConstraint(vec2 x, vec2 x2, float restlength, float stiffness)
{
    vec2 delta = x2 -x;
    float deltalength = length(delta);
    float diff = (deltalength-restlength) /deltalength;
    return delta*stiffness*diff;
}

vec2 summ_current_minus_rest_spring_len(vec2 f, vec2 R)
{
    vec2 summ_pos = vec2(.0);    
    vec2 fpos = texture(iChannel0, (f+vec2(0., 0.))/R.xy ).xy;
    float xrest = 1.;
    for(float i = 0.; i < 9.; i++)
    {
        vec2 cd = vec2(float(int((i))%3-1), float(int(i/3.)-1));
        vec2 idf = f + cd;
        if(idf.x < .0 || idf.x > NUM || idf.y < .0 || idf.y > NUM)
            continue;
        if (cd.x == cd.y && cd.x == 0.)
            continue;
        if (abs(cd.x) == abs(cd.y) && abs(cd.x) == 1.) // diagonals
            xrest = Xrd*DIST;
        else
            xrest = Xrs*DIST;
       	cd = texture(iChannel0, (f+cd)/R.xy ).xy; // need a vec2, reusing cd var
            summ_pos += DistanceConstraint(fpos, cd, xrest, .8105106125);
    }
	return summ_pos/8.;
}

void mainImage( out vec4 o, in vec2 f )
{
    particle parts;
    vec4	texture_buff;
    vec2	R = iResolution.xy;
    //o -= o;
    o = vec4(0.);
    if (f.x > NUM || f.y > NUM)
        discard;
    if (float(iFrame) <= 10.)
    {
		init(parts);
		init_pos(parts.pos, f);
	    o.xy += parts.pos;
		o.zw += parts.vit;
        return;
    }
    texture_buff = texture(iChannel0, (f/R.xy) ).xyzw;
    parts.pos = texture_buff.xy;
  	parts.vit = texture_buff.zw;
    if (f.y >= NUM-1.5 && (f.x <= 1. || f.x >= NUM-2. )) // top first and last node need to not moove
    {
        o = vec4(parts.pos, .0, .0);
        return;
    }
    parts.acc = 
            Ma*Gr*.0005
            +
            summ_current_minus_rest_spring_len(f, R)
            ;
    parts.vit.xy += parts.acc.xy;
	parts.pos.xy += parts.vit.xy;
	    	o.xy += parts.pos.xy;
			o.zw += parts.vit.xy;
}

// Be aware that NUM spawns NUM*NUM balls draw check and 2 * NUM * NUM lines draw check per pixel
#define NUM 10.

#define SC .25
