#define amp 0.9
#define transform 0.9

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord/iResolution.xy;

    vec3 col = texture(iChannel0, uv).rgb;
    vec3 newCol;

    float offset = 0.02;

    for (int i = 0; i < 3; i++) {
      	vec2 uv = uv;

        vec2 dist = vec2(
        	(
            	texture(iChannel0, uv)[i] - texture(iChannel0, uv + vec2(-1.0, 0.0) * offset)[i] +
            	texture(iChannel0, uv)[i] - texture(iChannel0, uv + vec2(1.0, 0.0) * offset)[i]
			) / 2.0,
        	(
            	texture(iChannel0, uv)[i] - texture(iChannel0, uv + vec2(0.0, -1.0) * offset)[i] +
            	texture(iChannel0, uv)[i] - texture(iChannel0, uv + vec2(0.0, 1.0) * offset)[i]
        	) / 2.0
		);

      	uv = (iMouse.xy / iResolution.xy) + dist * amp;
        uv = fract(uv);
        newCol[i] = texture(iChannel0, uv)[i];
    }

    fragColor = vec4(mix(newCol, texture(iChannel0, uv).rgb, 1.0 - transform), 1.0);
}
