#define PI 3.14159265359
#define quad 0.70710678118

#define R2 .6
#define ct cos(iTime*.2)
#define st sin(iTime*.3)
#define rot mat2(ct,st,-st,ct)
#define rot4 mat2(quad,quad,-quad,quad)

float ball(vec2 p, float r)
{
    float d=length(p);
    return (d<r)?smoothstep(0., 1.,(r-d)/r):0.;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord*2.0/iResolution.y - vec2(iResolution.x/iResolution.y, 1.0);

    vec2 u= vec2(sin(iTime),0.);
    float h=0.;

    h+=ball(uv, R2);
    for (float i=8.;i>0.;i-=1.)
        h+=ball(uv-(u*=rot4),R2*.5);

    u= vec2(sin(-iTime),0.);
    u*=inverse(rot);
    for (float i=8.;i>0.;i-=1.)
        h+=ball(uv-(u*=rot4),R2*.5);

    fragColor.rgb = vec3(smoothstep(.41, .4,h));
}
