#define CLEAN
#define S 2.

void mainImage( out vec4 c, in vec2 f )
{
    vec2 R = iResolution.xy, p = (2.*f-R)/R.y*20.0;
    vec2 d=iTime/S*floor(p.yx);
    #ifdef CLEAN
        bool b=mod(iTime,S*2.)-S>0.;
        d*=vec2(!b,b);
    #endif
    c.rgb = vec3(pow(1.0-length(fract(p+d)-.5),10.*f.x/R.x));
}
