#define PI 3.14159265359
#define VERTICALUNITAMOUNT 4.0
#define RADIUS 1.0
#define SPREADFACTOR 2.0

//see atan2 on wikipedia
vec2 cart2polar(vec2 a)
{
    float theta = 0.0;
    if(a.x > 0.0) {theta = atan(a.y/a.x);}
    if(a.x <= 0.0 && a.y >= 0.0) {theta = atan(a.y/a.x) + PI;}
    if(a.x <= 0.0 && a.y < 0.0) {theta = atan(a.y/a.x) - PI;}
    return vec2(length(a), mod(theta, 2.0*PI));
}

float colorSpike(float theta, float spike)
{
    return max(0.0, smoothstep(0.0, 1.0, 1.0 - abs(SPREADFACTOR*(theta - spike))));
}

//Try replacing two of the colors with 0.0 to see how colorSpike works.
//SPREADFACTOR should be chosen not too small, otherwise the colours won't blend properly together.
vec4 colorWheel(float theta)
{
    float thetamod = mod(theta, 2.0*PI)/(2.0*PI);
    return vec4(colorSpike(thetamod, 0.0) + colorSpike(thetamod, 1.0),
                colorSpike(thetamod, 1.0/3.0) + colorSpike(thetamod, 4.0/3.0),
                colorSpike(thetamod, 2.0/3.0) + colorSpike(thetamod, -1.0/3.0),
                1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.x - vec2(0.5, 0.5*iResolution.y/iResolution.x);
    uv *= VERTICALUNITAMOUNT;
    vec2 polaruv = cart2polar(uv);
    vec4 color = vec4(0.0);
    if(polaruv.x <= RADIUS)
    {
    	color = colorWheel(polaruv.y);
    }
    fragColor = color;
}
