vec2 rotate(vec2 v, float a) {
    float s = sin(a);
    float c = cos(a);
    mat2 m = mat2(c, -s, s, c);
    return m * v;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy - 0.5;
    uv.x*= iResolution.x / iResolution.y;
    float t = iTime;
    float z = t;
    uv = rotate(uv,z*0.1);

   float st = .1;
   vec2 fu = mod(fragCoord,100.0);
   float grid = step(length(fu-50.0),2.0);   



   vec2 p = vec2(cos(uv.y*12.11),sin(uv.x*11.4))*0.21;
   float k = length(uv - p);
   k -= t*0.07;

    float g = fract(k*7.34);
    vec3 col = vec3(1.2-g);
    col.gb -= step(mod(k,1.2),0.3);
    col.rg *= step(mod(k,1.),0.8);
    fragColor +=  vec4(col + grid*0.5,1.0);
}
