struct complex
{    
    float a, b;
};

//(a1+ib1)(a2+ib2) = a1*a2 + a1*ib2 + a2*ib1 - b1*b2
complex multiple(complex c1, complex c2)
{
    float a = (c1.a * c2.a) - (c1.b * c2.b);
	float b = (c1.a * c2.b) + (c2.a * c1.b);
    return complex(a,b);
}

complex add(complex c1, complex c2)
{
    return complex(c1.a + c2.a, c1.b + c2.b);
}

float norm(complex c)
{
	float dist = sqrt(c.a * c.a + c.b * c.b);
	return dist;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    int n = 18;
    complex z = complex(0.0,0.0);

    float scale = 5.0 - exp(sin(iTime));
    vec2 uv = vec2(scale, scale) * (fragCoord/iResolution.xy - vec2(0.5,0.5));
	complex c = complex(uv.x,uv.y);

    for(int i = 0; i < n; i++)
    {
    	z = add(multiple(z,z),c);	    
    }

    vec3 col;

    if(norm(z) < 5.0)
    	col = vec3(0.0,0.0,0.0);
	else
    	col = vec3(cos(norm(z)), sin(log(norm(z))), 0.0);


    fragColor = vec4(col,1.0);
}
