void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 col = vec3(0, 0.4, 0.6);
    vec3 bg = vec3(0, 0.3, 0.6);

    vec2 uv = fragCoord/iResolution.xy;
    uv -= 0.5;
    uv.x *= iResolution.x/iResolution.y;

    uv.x += sin(uv.y * 10.0+ iTime * 5.) * 0.002;
    uv.y += sin(uv.x * 10.0+ iTime * 5.) * 0.002;


    // base
    float d = length(uv);
    d -= iTime * 0.2;
    float n = 7.;
    d = fract(d * n);
    float w = 0.3 * length(uv);
    float b = 0.03;
    float r = 0.5 / n;
    float e = smoothstep(r, r-b, d);
	float s = smoothstep(r+w, r+w-b, d) - e;

    // light
    float l = max(uv.x*uv.y, 0.)*2. * s ;

    // highlight
    float w2 = 0.02+sin(l)*0.1;
    float h = smoothstep(r, r-b, d);
	h = smoothstep(r+w2, r+w2-b, d) - h;
    h *= l*10. + 0.2;


    col *= s;
    col += l;
    col += h;
    col += bg;



    fragColor = vec4(col, 1.);
}
