// draw repeatedly inscribed polygons; cf. https://doi.org/10.1016/S1385-7258(65)50004-4

// some constants

#define PI 3.14159265359

// array of colors

vec3[] cl = vec3[] (vec3(0.71, 0.09, 0.0), vec3(0.29, 0.22, 0.48), vec3(0.99, 0.99, 0.46), vec3(0.43, 0.52, 0.72), vec3(0.31, 0.44, 0.125), vec3(0.9, 0.72, 0.1), vec3(0.14, 0.18, 0.43), vec3(0.88, 0.03, 0.11), vec3(0.9, 0.87, 0.63));

// graphics primitives

#define drawPrimitive(p, c, d) d = mix(c, d, p)

float DISK(vec2 c, float r, vec2 p) // center c and radius r
{
    float sc = 4.0/iResolution.y;
    return smoothstep(-sc, 0.0, length(p - c) - r);
}

// regular polygon, adapted from https://www.shadertoy.com/view/tlXGW4

float regular( in int n, in vec2 p )
{
    float a = atan(p.y, p.x), ang = PI/float(n);

    return length(p) * cos(a - ang * (2.0 * floor(0.5 * a/ang) + 1.0)) - cos(ang);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float px = 4.0/iResolution.y;
	vec2 q = 1.1 * (2.0 * fragCoord - iResolution.xy)/iResolution.y;
    int n = 10; // maximum number of polygon sides

    vec3 col = vec3(0.98, 0.94, 0.9);      

    vec2 q1 = 1.5 * q - vec2(1.5, 0.0);
    float r = 1.0;

    for ( int k = 3; k <= n; k++ )
    {
          drawPrimitive(DISK(vec2(0.0), r, q1), cl[(2 * k - 6) % 9], col);
          col = mix(cl[(2 * k - 5) % 9], col, smoothstep(-px, px, regular( k, q1/r )));
          r *= cos(PI/float(k));
    }

    drawPrimitive(DISK(vec2(0.0), r, q1), cl[0], col);

    vec2 q2 = 1.5 * q + vec2(1.5, 0.0);
    r = 1.0;

    for ( int k = 0; k < n - 2; k++ )
    {
          int kk = n - k;
          drawPrimitive(DISK(vec2(0.0), r, q2), cl[(2 * k) % 9], col);
          col = mix(cl[(2 * k + 1) % 9], col, smoothstep(-px, px, regular( kk, q2/r )));
          r *= cos(PI/float(kk));
    }

    drawPrimitive(DISK(vec2(0.0), r, q2), cl[0], col);

	col *= 1.0 - 0.2 * length(fragCoord/iResolution.xy - 0.1); // vignetting

	fragColor = vec4( col, 1.0 );
}
