vec2 rotate(vec2 v, float a) {
  float s = sin(a);
  float c = cos(a);
  mat2 m = mat2(c, -s, s, c);
  return m * v;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;


    float x = iMouse.x/iResolution.x;
    float y = iMouse.y/iResolution.y;
    vec2 attractor = vec2(x, y);

    float angle = iTime * 0.1;

    vec2 uvMap = uv + iTime * 0.05;


    vec4 map = texture(iChannel1, uvMap);

    float dist = length(attractor - uv);
    float fade = exp(dist * -5.0) * 10.0;

    map *= 0.01 * fade;



    vec2 finalUV = vec2(uv.x + map.r, uv.y + map.g);


    vec4 col = texture(iChannel0, finalUV);

    fragColor = vec4(col.rgb, 1.0);
}
