// dot size
#define GRID_X (3.)
#define GRID_Y (3.)
const float dther_dm[16]=float[](
    0.0/16.0,	8.0/16.0,	2.0/16.0,	10.0/16.0,
        12.0/16.0,	4.0/16.0,	14.0/16.0,	6.0/16.0,
        3.0/16.0,	11.0/16.0,	1.0/16.0,	9.0/16.0,
        15.0/16.0,	7.0/16.0,	13.0/16.0,	5.0/16.0
);

bool dither( float x, float y, float c0 )
{
	x = mod( x, 4. );
	y = mod( y, 4. );
  if( c0 > ( dther_dm[ int(x) + int(y) * 4 ]) )return true;
  return false;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec2 pix;
    pix.x = floor( fragCoord.x/GRID_X ) * GRID_X;
    pix.y = floor( fragCoord.y/GRID_Y ) * GRID_Y;

	vec4 col = texture( iChannel0, pix/iResolution.xy );
    int xmod = int(mod( fragCoord.x, GRID_X ));
    switch( xmod ){
     case 2:
        if( dither( pix.x, pix.y, col.x ) )
        {
            col.x = 1.0;
        }else{ 
            col.x = 0.0;
        }
        col.y = 0.0;
        col.z = 0.0;
        break;

     case 1:
        if( dither( pix.x, pix.y, col.y ) )
        {
            col.y = 1.0;
        }else{
            col.y = 0.0;
        }
        col.x = .0;
        col.z = .0;
        break;

      default:
        if( dither( pix.x, pix.y, col.z ) )
        {
            col.z = 1.0;
        }else{
            col.z = 0.0;
        }
        col.x = 0.0;
        col.y = 0.0;
        break;
    }
	fragColor = col;

}
