// Fork of "FadePaint" by smkgames. https://shadertoy.com/view/Xd3fDr
// 2019-07-04

// see Buffer A tab; this just outputs the buffer

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	fragColor = texture(iChannel0, fragCoord.xy / iResolution.xy);
}

#define fadeTime 50.

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(distance(iMouse.xy, fragCoord) < 10.0 && iMouse.z > 0.0);
    fragColor += texture(iChannel0, fragCoord.xy / iResolution.xy);
    fragColor -= texture(iChannel0, fragCoord.xy / iResolution.xy * 0.9)*(1.0/fadeTime);
}
