#define MAX_ITERS 10
#define MAX_STEPS 512
vec3 sq3 (vec3 v) {
    return vec3(
        v.x*v.x-v.y*v.y-v.z*v.z,
        2.*v.x*v.y,
        2.*v.x*v.z
    );
}
vec2 sq (vec2 v) {
    return vec2(
        2.*v.y*v.x,
        v.y*v.y-v.x*v.x
    );
}
float squishy (float x) {
    return x*x*x;
}
float julia (vec3 p, vec3 c) {
    vec3 k = p;
    for(int i = 0; i<MAX_ITERS; i++) {
        k = sq3(k) + c;
        if(length(k)>10.) return 1.-squishy(float(i)/float(MAX_ITERS));
    }
    return -0.01;
}
vec2 rotate2D (vec2 p, float angle){
    return vec2(p.x*cos(angle)-p.y*sin(angle), p.y*cos(angle)+p.x*sin(angle));\
}
float mandelbrot (vec2 uv, vec2 c, float iters) {
    vec2 k = uv;
    for(float i = 0.; i<iters; i++) {
        k = sq(k)+c;
        if(length(k)>10.) return 1.-squishy(float(i)/iters);
    }
    return -1.;
}
