                 vec3(sin(iTime*0.3)*0.7, cos(iTime*0.4)*0.7, 0.1)
    );
*/
    //return length(p)-1.;
}
vec4 trace (vec3 o, vec3 r) {
    vec3 p = o;
    float t = 0.;
    float s;
    int i;
    for(i = 0; i<MAX_STEPS; i++) {
        p = o+r*t;
        s = sdf(p);
        t+=s*0.01;
		if(abs(s)<0.001||t>20.) break;
    }
    return vec4(p, float(i));
}
const float E = 0.1;
vec3 estimateNormal (vec3 p) {
    return normalize(vec3(
        sdf(vec3(p.x+E, p.y, p.z))-sdf(vec3(p.x-E, p.y, p.z)),
        sdf(vec3(p.x, p.y+E, p.z))-sdf(vec3(p.x, p.y-E, p.z)),
        sdf(vec3(p.x, p.y, p.z+E))-sdf(vec3(p.x, p.y, p.z-E))
    ));
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
	uv-=0.5;
    vec2 mouse = iMouse.xy/iResolution.xy;
    mouse-=0.5;
    float aspect = iResolution.y/iResolution.x;
    uv.x/=aspect;
    mouse.x/=aspect;
    vec3 cam = vec3(0., 0., -3.);
    vec3 ray = normalize(vec3(uv.xy*1.3, 1.));

    vec3 rot = vec3(mouse.y*5., -mouse.x*3., 0.);

    cam.xz = rotate2D(cam.xz, rot.y);
    ray.xz = rotate2D(ray.xz, rot.y);

    cam.zy = rotate2D(cam.zy, rot.x);
    ray.zy = rotate2D(ray.zy, rot.x);


    vec4 t = trace(cam, ray);
    vec3 e = t.xyz;
    vec3 light = vec3(0., 10., -10.);
    vec3 toLight = normalize(light-e);
    vec3 norm = estimateNormal(e);
    float diffuse = max(0.,dot(toLight, norm))*0.8;
    vec3 refl = reflect(ray, norm);
    float specular = pow(max(0.0, dot(refl, toLight)), 4.0)*0.5;
    float d = length(e-cam);
    float fog = 1.0 / (1.0 + d*d*0.01);

    vec3 col = vec3(1.);//texture(iChannel0, refl).rgb*1.+0.8;
    col -= pow(t.w/float(MAX_STEPS), 2.)*0.8;//ao
    col *= (diffuse+specular)*0.+0.8;//other light
    col = 0.01+(col-0.01)*3.6;
    col *= fog;

    // Output to screen
    fragColor = vec4(col,1.0);
}
