float box(vec2 p)
{
    p=abs(p)-2.;
    return max(p.x,p.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float time = iTime*.25;

    fragColor = vec4(0);
    for (int i = 0; i < 10; ++i)
    {
        vec2 uv = fragCoord/iResolution.xy-.5;
        uv.x *= iResolution.x/iResolution.y;
        uv *= 30. * pow(2.,-fract(time));

        vec4 pos = 4.-3.*smoothstep(
            vec4(0.10, 0.30, 0.50, 0.70),
            vec4(0.30, 0.50, 0.70, 0.90),
            vec4(fract(time))
        );

        float a = box(uv - pos.x * vec2(-1, 1));
        float b = box(uv - pos.y);
        float c = box(uv - pos.z * vec2( 1,-1));
        float d = box(uv + pos.w);

        fragColor += step(a*b*c*d, 0.);
        time += .1/60.;
    }
    fragColor *= .1;
}
