// variant of https://shadertoy.com/view/4dySW3

void mainImage(out vec4 O,  vec2 u ){

    vec2 z = iResolution.xy, A = vec2(2,1),                   // brick aspect
         U = (2.*u - z) / z.y;                                // normalized coordinates

	z = U - vec2(-1,0);  U.x -= .5;                           // Moebius transform
    U *= mat2(z,-z.y,z.x) / dot(U,U);

                  // offset   spiral, zoom   phase            // spiraling
    U =   log(length(U+=.5))*vec2(.5, -.5) + iTime/8.
        + atan(U.y, U.x)/6.2832 * vec2(6, 1);        
	                               // n  
    z = U *= 3./A; 
    if ( fract(iTime/10.)>.5 ) U=U.yx;                        // bricks direction
    if ( fract(U.y) > .5 ) U.x+=.5;                           // bricking
    U *= 2./A;
    U = (1.-abs(fract(U)*2.-1.))*A; float v = min(U.x,U.y);   // dist to border
    O = texture(iChannel0,z).rrrr;                            // grain
    O =   smoothstep(-.7,.7, (v-.05)/min(.3,fwidth(z.y*4.)))  // draw AA bricks
        * vec4(.6,.2,0,0) * (.5+.5*O) + .3*O;                 // color & grain
}
