float circle(vec2 p,float r){
    float d = length(p);

    // comment/uncomment lines 6 and 7 for variations

    //d = abs(smoothstep(r,r+0.05,d)-smoothstep(r - 0.1,r - 0.1 + 0.05,d));
    d = 1.0-abs(smoothstep(r,r+0.05,d));
    return d;
}

vec2 rot(float a, vec2 c){

    vec2 r = c * mat2(sin(a),-sin(a),sin(a),cos(a));

    return r;

}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = (fragCoord-0.5*iResolution.xy)/iResolution.y;
    vec3 col = vec3(0.5,0.1,0.4);

    uv = uv * 8.0;
    uv += 0.5;

    uv = rot(-iTime,uv);

    vec2 gv = fract(uv)-0.5;
    vec2 id = floor(uv);



    float d = circle(gv,0.5-(abs(id.x+id.y)*sin(iTime*3.0)*0.05+0.5)*0.05);

    col += d;
	col *= vec3(0.6,0.4,0.8);

    fragColor = vec4(col,1.0);
}
