// Modified version from https://rubendv.be/posts/fwidth/

//#define ALG0_1 1
#define ALG0_2 3
//#define ALG0_3 1


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(0.2, 0.2, 0.2, 1.0);

    vec4 colors[4] = vec4[](
        vec4(1.0, 0.0, 0.0, 1.0), 
        vec4(0.0, 1.0, 0.0, 1.0), 
        vec4(0.0, 0.0, 1.0, 1.0), 
        vec4(0.0, 0.0, 0.0, 1.0)
    );

    vec2 uv = fragCoord.xy * 2.0 / iResolution.y - 1.0;

#ifdef ALG0_1    
    for(int row = 0; row < 2; row++) {
        for(int col = 0; col < 2; col++) {
            float dist = distance(uv, vec2(-0.50 + float(col), 0.50 - float(row)));
            float alpha = step(0.45, dist);
            fragColor = mix(colors[row*2 + col], fragColor, alpha);
        }
    }
#endif    

#ifdef ALG0_2
    for(int row = 0; row < 2; row++) {
        for(int col = 0; col < 2; col++) {
            float dist = distance(uv, vec2(-0.50 + float(col), 0.50 - float(row)));
            float delta = (sin(iTime/15.0) + 1.0) / 2.0;
            float alpha = smoothstep(0.45-delta, 0.45, dist);
            fragColor = mix(colors[row*2 + col], fragColor, alpha);
        }
    }    
#endif

#ifdef ALG0_3
    for(int row = 0; row < 2; row++) {
        for(int col = 0; col < 2; col++) {
            float dist = distance(uv, vec2(-0.50 + float(col), 0.50 - float(row)));
            float delta = fwidth(dist);
            float alpha = smoothstep(0.45-delta, 0.45, dist);
            fragColor = mix(colors[row*2 + col], fragColor, alpha);
        }
    }    
#endif


}
