void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Output to screen
    vec4 colorC = texture(iChannel1, uv);
    vec4 colorA = texture(iChannel0, uv);

    float avc = (colorC.r + colorC.g + colorC.b) / 3.0;
    vec3 greyC = vec3(avc, avc, avc);

    fragColor = vec4(colorC.rgb * colorA.a + greyC * greyC * (1.0 - colorA.a), 0.0);
}

float depth(vec4 color) {
	return min(1.0, 1.1 - (color.r + color.g + color.b) / 3.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec2 uvxp = (fragCoord + vec2(1.0, 0.0))/iResolution.xy;
    vec2 uvxn = (fragCoord + vec2(-1.0, 0.0))/iResolution.xy;
    vec2 uvyp = (fragCoord + vec2(0.0, 1.0))/iResolution.xy;
    vec2 uvyn = (fragCoord + vec2(0.0, -1.0))/iResolution.xy;

    // Output to screen
    vec4 colorLocal = texture(iChannel0, uv) - texture(iChannel2, uv) * 0.2;

    vec4 colorCurrent = texture(iChannel1, uv);
    vec4 colorxp = texture(iChannel1, uvxp);
    vec4 colorxn = texture(iChannel1, uvxn);
    vec4 coloryp = texture(iChannel1, uvyp);
    vec4 coloryn = texture(iChannel1, uvyn);

    if (iFrame == 0 && fragCoord.x * fragCoord.y < 2.0) {
    	fragColor = vec4(0.0, 0.0, 0.0, 1.0);
    } else if (iFrame == 0) {
    	fragColor = vec4(0.0, 0.0, 0.0, 0.0);
    } else {
        float adj = colorxp.a + colorxn.a + coloryp.a + coloryn.a;
        adj = adj * depth(colorLocal) * depth(colorLocal) * depth(colorLocal);
        float self = colorCurrent.a;

        float newA = min(1.0, adj / 4.0 + self * adj + self);
    	fragColor = vec4(0.0, 0.0, 0.0, newA);
    }
}
