#define hsv2rgb(h) clamp( abs(mod( h*6.+vec3(0,4,2), 6.)-3.)-1., 0., 1. )

vec2 calcInfluence( vec4 ball, vec2 uv)
{ 
    float d = distance(ball.rg, uv);
    ball.b /= d*d*d;
    return  ball.ab;   
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.y;
    uv.x -= 0.333;

   	float threshold = 100.0;
    float reduce = 0.4;

    float bars = 18.0;
    int i_bars = int(bars);

    float winningColor = 0.0;
    float currentInfluence = 0.0;

   	float totalPositive = 0.0;
    float totalNegative = 0.0;
    float totalShade = 0.0;

    //first pass determine color with greatest influence
    for( int i = 0; i < i_bars; i++ ){

        float i_float = float(i);
        vec4 p0 = texture(iChannel0, vec2(i_float/bars + (1.0/bars*0.5),0.0));
        vec2 ball0 = calcInfluence(p0, uv);

        if( ball0.y > currentInfluence ){
           winningColor = ball0.x;
           currentInfluence = ball0.y;     
        }


    }

    for( int i = 0; i < i_bars; i++ ){

        float i_float = float(i);   
        vec4 p0 = texture(iChannel0, vec2(i_float/bars + (1.0/bars*0.5),0.0));
        vec2 ball0 = calcInfluence(p0, uv);

        //same color as current winner, add to it
        if( ball0.x == winningColor ){
            totalPositive += ball0.y;

        }
        else{
            totalNegative += ball0.y;
        }


    }


    totalShade = pow( totalShade, 1.0/3.0);


    float influence = totalPositive - totalNegative * reduce;

    float step = smoothstep( 0., min(1.,2.*fwidth(influence)), influence - threshold);

    fragColor = vec4( hsv2rgb( winningColor ) * step, 1.0 );

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{


    vec2 uv = fragCoord/iResolution.xy;
    float rad = 0.15;
    float bars = 18.0;

   	float hue1 = 0.04;
    float hue2 = 0.6;
    float hue3 = 0.2;
    float hue4 = 0.9;

    vec4 mouse = iMouse / iResolution.yyyy;
    mouse.x -= 0.333;

    vec4 color;

    float jiggle = sin(iTime*3.0 + uv.x) * 0.01;

    if(uv.x < 1.0/bars)
        color = vec4(0.2 + jiggle,0.2 - jiggle,rad, hue1);

    else if(uv.x < 2.0/bars)
        color = vec4(0.2 - jiggle,0.4,rad, hue4);

    else if(uv.x < 3.0/bars)
        color = vec4(0.2,0.6 + jiggle,rad, hue4);

   	else if(uv.x < 4.0/bars)
        color = vec4(0.2 - jiggle,0.8 - jiggle,rad, hue1);

    else if(uv.x < 5.0/bars)
        color = vec4(0.4 + jiggle,0.2 - jiggle,rad, hue1);

    else if(uv.x < 6.0/bars)
        color = vec4(0.4 + jiggle,0.4,rad, hue3);

    else if(uv.x < 7.0/bars)
        color = vec4(0.4,0.6 + jiggle,rad, hue1);

    else if(uv.x < 8.0/bars)
        color = vec4(0.4 - jiggle,0.8,rad, hue1);

    else if(uv.x < 9.0/bars)
        color = vec4(0.6,0.2 - jiggle,rad, hue2);

    else if(uv.x < 10.0/bars)
        color = vec4(0.6,0.4 - jiggle,rad, hue3);

    else if(uv.x < 11.0/bars)
        color = vec4(0.6 - jiggle,0.6 - jiggle,rad, hue3);

    else if(uv.x < 12.0/bars)
        color = vec4(0.6,0.8 - jiggle,rad, hue2);

    else if(uv.x < 13.0/bars)
        color = vec4(0.8 + jiggle,0.2 + jiggle,rad, hue1);

    else if(uv.x < 14.0/bars)
        color = vec4(0.8 + jiggle,0.4 - jiggle,rad, hue3);

    else if(uv.x < 15.0/bars)
        color = vec4(0.8,0.6 + jiggle,rad, hue2);

    else if(uv.x < 16.0/bars)
        color = vec4(0.8,0.8 - jiggle,rad, hue2);

    else if(uv.x < 17.0/bars)
        color = vec4( mouse.xy, rad, hue3);

   	else 
    	color = vec4( abs(vec2(sin(iTime*0.25), cos(iTime*0.15))),rad, hue2);


    fragColor = color;


}
