float curve(float x) {
    float id = pow(floor(sqrt(abs(x))),2.);
    float sgn = mod(id,2.)*2.-1.;
    return sgn*(x-sign(x)*(id+sqrt(id)));
}

//thanks to @jay@cybre.space !
//https://cybre.space/@jay/104367634214778284
float curve_fractal(float x) {
    float id = floor(log2(abs(x)));
    float sgn = sign(mod(id,2.)-.5);
    return (4./3.*pow(2.,id)-abs(x))*sgn;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*vec2(iResolution.x,0))/iResolution.y;
    bool top = uv.y<.5;
    uv.y = mod(uv.y,.5)-.25;
    uv *= 90.;

    float scale = mix(1.,10.,sin(iTime)*.5+.5);
    float y = top ? curve_fractal(uv.x*scale) : curve(uv.x*scale);
    float col = smoothstep(.2,.3,abs(y-uv.y*scale)/scale);
    col -= smoothstep(.3,.1,abs(uv.y))*.5;

    // Output to screen
    fragColor = vec4(col);
}
