void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = (fragCoord - 0.5 * iResolution.xy ) / iResolution.y;

    // atan( uv.x , uv.y ) : -pi ~ pi so it have to remap 0 ~ 1 by diving 6.2813 + 0.5f
    vec2 st = vec2( atan(uv.x , uv.y ) , length(uv) );
    uv = vec2( st.x / 6.2831f + 0.5f + iTime * 0.1f + st.y * 2.0f , st.y );

    float x = uv.x * 8.0f;
    float m = min( fract(x) , fract(1.0-x) ); // 0.0f ~ 0.5f : remember this expression
    float c = smoothstep( 0.0f, 0.01f , m * 0.3f + 0.1f - uv.y );	// zig zag partten


    // Zig zag pattern
    //float c = smoothstep( 0.0f, 0.1f , m - uv.y );	// zig zag partten

    // Line pattern
    //float c = smoothstep( 0.0f , 0.1f , m);


    //fragColor = vec4(st.x / 6.2831f + 0.5f );	// -pi ~ pi => Remap 0  ~ 1
    fragColor = vec4(c );	// -pi ~ pi => Remap 0  ~ 1
}
