#define EPSILON 0.01

vec2 proy(in vec2 a, in vec2 b){
    return b * dot(b, a) / dot(b, b);
}

vec2 proy01(in vec2 a, in vec2 b){
    return b * clamp(dot(b, a) / dot(b, b), 0., 1.);
}

float SDFSegmento(vec2 p, vec2 a, vec2 b){
    vec2 v = p - a;
    vec2 w = b - a;
    return length(v -  proy01(v, w));
}

// Simetría sobre el origen
vec2 simetria(vec2 n, vec2 p){    
    return p + 2. * (proy(p, n) - p);
}
// Sobre cualquier recta genérica
vec2 simetria(vec2 p, vec2 a, vec2 b){
    return a + simetria(b - a, p - a);
}

float escena_sdf(vec2 p){
    // Recta Simetría
    vec2 a = vec2(0.2, 0.2);
    vec2 b = vec2(0.0, 0.1);
    // Simetría
    vec2 ps = simetria(p, a, b);

	return SDFSegmento(ps, vec2(-0.2, -0.2), vec2(0.3, 0.4));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Preservamos el ratio aspecto y re-centramos
    vec2 uv = (fragCoord-iResolution.xy * 0.5)/min(iResolution.x, iResolution.y);
    // Para cada punto p (uv), aplicamos la funcion de distancia con signo
    float d = escena_sdf(uv);

    vec3 col;
    if(abs(d) < EPSILON){
        col = vec3(1.0);
    }else{
        if(d < 0.0){
    		col = vec3(1.0, 0., 0.);
    	}else{
    		col = vec3(0., 0., 1.0);
    	}
        // Numero de repeticiones
        float k = 30.0;
        // Tomamos la distancia en valor absoluto y nos quedamos con la parte fraccional
        // Este valor esta [0,1].
        col = col * (0.5 + 0.5 * (fract(abs(d) * k)));
    }

    // Devolvemos el color asignado
    fragColor = vec4(col,1.0);
}
