float distLine( vec2 p, vec2 a, vec2 b){
	vec2 pa = p - a;
    vec2 ba = b - a;
    float t = clamp(dot(pa, ba)/dot(ba, ba), 0.0, 1.0);
    return length(pa - ba*t);
}

float noiseFloat(vec2 p){
    p = fract(p * vec2(213.53, 970.19));
    p = p + dot(p, p+548.23);
    return fract(p.x * p.y);
}

vec2 noiseVector(vec2 p){
	float n = noiseFloat(p);
    return vec2(n, noiseFloat(p+n));
}


vec2 GetPos(vec2 id, vec2 offsets){
	vec2 n = noiseVector(id + offsets) * iTime;
    return sin(n) * 0.4 + offsets;
}

float Line(vec2 p, vec2 a, vec2 b){
	float d = distLine(p, a, b);
    float m = smoothstep(0.03, 0.002, d);
    m = m * smoothstep(1.5, 0.1, length(a-b));
    return m;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord -1.0*iResolution.xy)/iResolution.y;

    float m = 0.0;
    uv = uv * 10.0;
    vec2 gv = fract(uv) - 0.5;
    vec2 id = floor(uv);

    vec2 p[9];

    int i = 0;
    for(float y = -1.0; y<=1.0; y++){
    	for(float x = -1.0; x<=1.0; x++){
            p[i++] = GetPos(id, vec2(x, y));
        }
    }

    for(int i=0; i<9; i++){
    	m = m + Line(gv, p[4], p[i]);

        vec2 j = (p[i] - gv) * 28.0;
        float sparkle = 1.0/dot(j, j);

        m = m + sparkle * (sin((iTime+p[i].x)*3.0)*0.5 + 0.5);
    }

    m = m + Line(gv, p[1], p[3]);
    m = m + Line(gv, p[1], p[5]);
    m = m + Line(gv, p[7], p[3]);
    m = m + Line(gv, p[7], p[5]);

    vec3 col = vec3(0.0, m, 0.0);    

    // Output to screen
    fragColor = vec4(col,1.0);
}
