vec2 Nth_weyl(int n) {
    return fract(vec2(n*12664745, n*9560333)/exp2(24.));
}

float logo(vec2 uv) {
    float scale = 10.5;
    int roundedy = int(floor(uv.y * scale + 0.5));
    float roundedy_float = (float(roundedy)) / scale;

	float radius = pow(smoothstep(0.0, 1.0, iTime/1.25),0.5);
    float thickness = 0.1;

    bool white = abs(length(uv) - radius) > thickness*0.7;

    if (uv.x < radius-1.0) {
        if (length(uv) < radius) {
            white = false;
        }
    }
    vec2 uv2 = vec2(uv.x, roundedy_float);
    if (length(uv2*vec2(1.0,0.8) + vec2(0.75, 0.0)) < radius) {
        if (length(uv2) < radius - thickness) {
            white = roundedy % 2 == 0;
        }
	}
    return white ? 1.0 : 0.0;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy*2.0-1.0;
	uv.x *= iResolution.x/iResolution.y;
    float globalscale = 0.5;
    uv /= globalscale;
    float pixelsize = (1.0/globalscale)/iResolution.y*2.5;

    float logoval = 0.0;
    int iterations = 32;
    for (int i = 0; i < iterations; i++) {
        logoval += logo(uv + Nth_weyl(i)*pixelsize);
    }
    logoval /= float(iterations);

    // Time varying pixel color
    vec3 col = vec3(logoval);

    // Output to screen
    fragColor = vec4(col,1.0);
}
