void mainImage( out vec4 Q, in vec2 U )
{
    vec4 a = A(U);
    vec4 b = B(U);
    vec4 c = C(U);
    vec4 d = D(U);
    c = vec4(c.x,0,c.y,1);
    d = vec4(d.x,0,d.y,1);
    Q = (sqrt(a*a+b*b+c*c+d*d));
    Q.xyz = mix(Q.xyz,normalize(Q.xyz),min(1.,length(Q.xyz)));
	Q *= vec4(.7,1,1.2,1);
}

void mainImage( out vec4 Q, in vec2 U )
{
    Q = A(U);
    vec4 b = B(U);
    vec4 dm = 1./4.*(
    	A(U+vec2(1,0))+
        A(U+vec2(0,1))+
        A(U-vec2(1,0))+
        A(U-vec2(0,1))
    )-Q;
    Q += b + .01*dm;
    if (iFrame < 1) Q = sin(.1*length(U-0.5*R)*vec4(1,2,3,4));
	if(iMouse.z>0.&&length(U-iMouse.xy)<16.||(length(U-0.5*R)<2.&&iFrame<2)) Q = sin(4.*iTime*vec4(1,2,3,4));
}

void mainImage( out vec4 Q, in vec2 U )
{
    Q = B(U);
    vec4 a = A(U);
    vec4 c = C(U);
    vec4 dm = 1./4.*(
    	A(U+vec2(1,0))+
        A(U+vec2(0,1))+
        A(U-vec2(1,0))+
        A(U-vec2(0,1))
    )-a;
    vec4 dmb = 1./4.*(
    	B(U+vec2(1,0))+
        B(U+vec2(0,1))+
        B(U-vec2(1,0))+
        B(U-vec2(0,1))
    )-Q;
    Q += dm + .01*dmb;
    if (length(a.xyz)>0.) Q.xyz -= .03333*a.xyz/length(a.xyz)+.02*a.xyz/sqrt(dot(c.xy,c.xy)+dot(a.xyz,a.xyz));
}

