void mainImage( out vec4 Q, in vec2 U )
{
    Q = D(U);
    vec4 a = A(U);
    vec4 c = C(U);
    vec4 dm = 1./4.*(
    	C(U+vec2(1,0))+
        C(U+vec2(0,1))+
        C(U-vec2(1,0))+
        C(U-vec2(0,1))
    )-c;
