void mainImage(out vec4 O, vec2 C) {
    O=vec4(0);
	ivec2 p=ivec2(700.*C/iResolution.y);
    int x=p.x,y=p.y,T=int(iTime*250.),
    n=T+abs((x+y-T)&(x-y+T)^((x+y-T/3)|(x-y+T/3)));
    O.xyz+=mix(vec3(1),(smoothstep(1200.,0.,vec3(n%1400,n%1900,n%1800))),.8)*.6+
        .9*fwidth(step(20.,float(n%999)));}
