#define P 3.14159265359
mat2 r(float a) { float c = cos(a), s = sin(a); return mat2(c,s,-s,c); }
float sq(vec2 p, vec2 sz, float r) { return length(max(abs(p) - sz,0.)) - r; }

float anim1(float x) { float sp = P*12.; x = x*sp; return (x + sin(x - P))/sp; }

vec2 m;
float t;
float aa;
float shape(vec2 p) {
    float d = 10e+9;
    vec2 s1 = vec2(0.,(0.34444));
    float s2 = .3;
    for(int i = 0; i < 8; i++) {
        float d1 = min(d,sq(p, s1, .002));
        float d2 = sq(p*r(P*.25),vec2(.15)*s2, max(.001,aa));
        d2 = min(d2,sq(p*r(P*.25) + .5*s2,vec2(.075)*s2, max(.001,aa)));
        d2 = min(d2,sq(p*r(P*.25) - .5*s2,vec2(.075)*s2, max(.001,aa)));
        float d3 = abs(length(p) - .7155*s2) - .001;

        d =  min(d1,d2);
        d =  min(d,d3);

        p *= r(P*anim1(-t*.125)*.5);
        p *= r(P*-0.3333);
        p.y = abs(p.y) - s2*m.y;
        p.x = abs(p.x) - s2*m.x;
        p *= r(P);
        s2 *= 0.5;
        s1.y *= 0.496;
    }

    //d = abs(d - .001) - .0005;
    float dd = d;
    d = smoothstep(-aa,aa,d);
    float color = mix(0.,1.,d);

    return color;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 st = fragCoord.xy/iResolution.xy - .5;
    st.x *= iResolution.x/iResolution.y;
    t = iTime;
    m = iTime < .1 ? vec2(1.) : iMouse.xy/iResolution.xy - .1;
    aa = (1./iResolution.x);

    vec3 color = vec3(0.);
    color.r += shape(st + vec2(aa + length(st*.002),0.));
    color.g += shape(st + vec2(0.,0.));
    color.b += shape(st + vec2(0.,aa + length(st*.002)));

    fragColor = vec4(color - pow(length(st)*.9,1.),1.0);
}
