vec2 rotate(float a, vec2 v) 
{
	float c_a = cos(a);
	float s_a = sin(a);
	float x1 = v.x * c_a + v.y * s_a;
	float y1 = v.x * -s_a + v.y * c_a;
	v.x = x1;
	v.y = y1;
    return v;
}
vec2 scale(float s1, float s2, vec2 v) 
{
	v.x /= s1;
	v.y /= s2;
    return v;
}

vec2 translate(vec2 dv, vec2 v) 
{
	v.x -= dv.x;
	v.y -= dv.y;
    return v;
}

float axes(float x, float y)
{
    float axis_y;
    float axis_x;
    if(abs(x)< 0.1) axis_y=1.0;
    if(abs(y)< 0.1) axis_x=1.0;
    return   axis_y+axis_x;           
}

float grid(float x, float y)
{
    float g_y;
    float g_x;
    if(abs(mod(x,1.0)) < 0.15) g_x=1.0;
    if(abs(mod(y,1.0)) < 0.15) g_y=1.0;
    return (g_y+g_x)/3.0;
}

float quadrangle(float x,float y) {
	return 1.0 - max(abs(x),abs(y));
}

float triangle(float x,float y) {
	return min(0.0 - (x + y),min( 1.0 + x, 1.0 + y));
}
//буква в профиль
float letter1(float x, float y)
{
    //палочка
    vec2 j1 = vec2(x, y);
	j1 = translate(vec2(5, 8), j1);
	j1 = scale(3.0, 6.0, j1);
	float jj1 = quadrangle(j1[0], j1[1]);


	vec2 j2 = vec2(x, y);
	j2 = translate(vec2(8.3,13.4), j2);
	j2 = rotate(3.14 / 2.0, j2);
    j2 = rotate(3.14 / 2.0, j2);
    j2 = rotate(3.14 / 2.0, j2);
    j2 = rotate(3.14 / 2.0, j2);
    j2 = rotate(3.14 / 2.0, j2);
	j2 = scale(0.8, 0.8, j2);
	float jj2 =triangle(j2[0], j2[1]);


    vec2 j3 = vec2(x, y);
	j3 = translate(vec2(8.5,2.7), j3);
	j3 = rotate(3.14 / 2.0, j3);
    j3 = rotate(3.14 / 2.0, j3);
    j3 = rotate(3.14 / 2.0, j3);
	j3 = scale(0.8, 0.8, j3);
	float jj3 =triangle(j3[0], j3[1]);


    vec2 ch1 = vec2(x, y);
	ch1 = translate(vec2(8.5,8.3), ch1);
	ch1 = scale(0.5, 5.3, ch1);
	float chh1 = quadrangle(ch1[0], ch1[1]);

     vec2 j5 = vec2(x, y);
	j5 = translate(vec2(11.5, 8), j5);
	j5 = scale(2.5, 6.0, j5);
	float jj5 = quadrangle(j5[0], j5[1]);



	return max(jj1,max(jj2,max(jj3,max(chh1,jj5))));
}
//буква ю слева

float letter2(float x, float y)
{
    //палочка ю
    vec2 j1 = vec2(x, y);
	j1 = translate(vec2(-15, 8), j1);
	j1 = scale(1.0, 6.0, j1);
	float jj1 = quadrangle(j1[0], j1[1]);

    //перепонка у ю
	vec2 j2 = vec2(x, y);
	j2 = translate(vec2(-11.2,8), j2);
	j2 = rotate(3.14 / 2.0, j2);
	j2 = scale(1.0, 3.0, j2);
	float jj2 = quadrangle(j2[0], j2[1]);


    vec2 j3 = vec2(x, y);
	j3 = translate(vec2(-9,8), j3);
	j3 = scale(1.0, 6.0, j3);
	float jj3 = quadrangle(j3[0], j3[1]);

    vec2 j4 = vec2(x, y);
	j4 = translate(vec2(-6.2,13), j4);
	j4 = rotate(3.14 / 2.0, j4);
	j4 = scale(1.0, 3.0, j4);
	float jj4 = quadrangle(j4[0], j4[1]);

    vec2 j5 = vec2(x, y);
	j5 = translate(vec2(-4,8), j5);
	j5 = scale(1.0, 6.0, j5);
	float jj5 = quadrangle(j5[0], j5[1]);


    vec2 j6 = vec2(x, y);
	j6 = translate(vec2(-7,3), j6);
	j6 = rotate(3.14 / 2.0, j6);
	j6 = scale(1.0, 3.0, j6);
	float jj6 = quadrangle(j6[0], j6[1]);


	return max(jj1,max(jj2,max(jj3,max(jj4,max(jj5,jj6)))));
}
//буква ч
float letter3(float x, float y)
{
    vec2 ch1 = vec2(x, y);
	ch1 = translate(vec2(-6.5, -8), ch1);
	ch1 = scale(3.3, 6.0, ch1);
	float chh1 = quadrangle(ch1[0], ch1[1]);



    vec2 ch2 = vec2(x, y);
	ch2 = translate(vec2(-15, -5), ch2);
	ch2 = scale(1.0,3.0, ch2);
	float chh2 = quadrangle(ch2[0], ch2[1]);


    vec2 ch3 = vec2(x, y);
	ch3 = translate(vec2(-11,-8.5), ch3);
	ch3 = rotate(3.14 / 2.0, ch3);
	ch3 = scale(1.0,3.2, ch3);
	float chh3 = quadrangle(ch3[0], ch3[1]);


  	vec2 ch4 = vec2(x, y);
	ch4 = translate(vec2(-15.2, -8.5), ch4);    
	ch4 = rotate(3.14 / 1.0, ch4);
	ch4 = scale(1.0,0.9, ch4);
	float chh4 = triangle(ch4[0], ch4[1]);

	return max(chh1,max(chh2,max(chh3,chh4)));

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    float wmax=max(iResolution.x,iResolution.y);
    float aspect =iResolution.y/iResolution.x;
    vec2  vnorm = vec2(wmax,wmax);   
    vec2 uv = fragCoord/vnorm ; //iResolution.xy;
    float x=uv.x;
    float y=uv.y;

    x*=60.0;
    y*=60.0;
    x-=30.0;
    y-=30.0*aspect;


    float grid =  max(axes(x,y),grid(x,y))/2.0;    


    float f=letter1(x, y);
    f=max(f, letter2(x, y));
    f=max(f, letter3(x, y));
    f = 0.05/f; 

    // Time varying pixel color

    vec3 col = vec3(0.0, grid, 0.0);

    if(f >= 0.0)col.x =  f;
    else col.z = -f;

    // Output to screen
    fragColor = vec4(col, 1.0);
}
